var express = require('express'),
    api = require('./middleware/api/index'),
    app = require('./middleware/app/index'),
    routes = require('./routes'),
    config = require('./config');

module.exports = function(options) {
  // Store config inside config store
  config.store(options);

  return {
    api: api,
    app: app,
    routes: routes
  };
}
